import styled from "styled-components";
import React from "react";
import LogoLightImg from "../images/글씨 (검정).png";
import LogoDarkImg from "../images/글씨 (하양).png";
import SunLightImg from "../images/sun_colored.png";
import SunDarkImg from "../images/sun_white.png";
import MoonLightImg from "../images/moon.png";
import MoonDarkImg from "../images/moon_colored_white.png";
import MenuLightImg from "../images/menu_dark.png";

const TopBarContainer = styled.div`
    width: 100%;
    //max-width: 1920px;
    height: 80px;
    padding: 0 1vw;
    //background: #F0F0F0;
    background: ${({ darkMode }) => (darkMode ? "#000" : "#F0F0F0")};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const LogoImg = styled.img`
    width: 65px;
    aspect-ratio: 1 / 1;
`

const Right = styled.div`
    display: flex;
    align-items: center;
    gap: 2vw;
`

const BgChangeBtn = styled.div`
    width: 80px;
    height: 30px;
    display: flex;
    justify-content: space-between;
`

const ToggleImg = styled.img`
    height: 100%;
    aspect-ratio: 1 / 1;
`

const MenuImg = styled.img`
    width: 50px;
`

const TopBar = ({darkMode, setDarkMode}) => {
    const toggleDarkMode = () => {
/*        setDarkMode(!darkMode);
        document.documentElement.classList.toggle('dark', !darkMode);*/
        if (!darkMode) {
            setDarkMode(true);
            document.documentElement.classList.add('dark');
        }
    };

    const toggleLightMode = () => {
        if (darkMode) {
            setDarkMode(false);
            document.documentElement.classList.remove('dark');
        }
    };

    return (
        <TopBarContainer darkMode={darkMode}>
            {/*<LogoImg src={LogoLightImg}/>*/}
            <LogoImg src={darkMode ? LogoDarkImg : LogoLightImg} />
            <Right>
                <BgChangeBtn>
{/*                    {!darkMode ? (
                        <ToggleImg src={SunLightImg} onClick={toggleDarkMode} />
                    ) : (
                        <ToggleImg src={SunDarkImg} />
                    )}
                    {!darkMode ? (
                        <ToggleImg src={MoonLightImg} onClick={toggleDarkMode} />
                    ) : (
                        <ToggleImg src={MoonDarkImg} />
                    )}*/}
                    <ToggleImg
                        src={darkMode ? SunDarkImg : SunLightImg}
                        onClick={darkMode ? toggleLightMode : null}
                    />
                    <ToggleImg
                        src={darkMode ? MoonDarkImg : MoonLightImg}
                        onClick={!darkMode ? toggleDarkMode : null}
                    />
                </BgChangeBtn>
                <MenuImg src={MenuLightImg}/>
            </Right>
        </TopBarContainer>
    );
}

export default TopBar;
