import styled from "styled-components";
import React from "react";
import LogoLightImg from "../images/글씨 (검정).png";
import SunLightImg from "../images/sun_colored.png";
import SunDarkImg from "../images/sun_white.png";
import MoonLightImg from "../images/moon.png";
import MoonDarkImg from "../images/moon_colored_white.png";
import MenuLightImg from "../images/menu_dark.png";

const TopBarContainer = styled.div`
    width: 100%;
    //max-width: 1920px;
    height: 80px;
    padding: 0 1vw;
    background: #F0F0F0;
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const LogoImg = styled.img`
    width: 65px;
    aspect-ratio: 1 / 1;
`

const Right = styled.div`
    display: flex;
    align-items: center;
    gap: 2vw;
`

const BgChangeBtn = styled.div`
    width: 80px;
    height: 30px;
    display: flex;
    justify-content: space-between;
`

const ToggleImg = styled.img`
    height: 100%;
    aspect-ratio: 1 / 1;
`

const ToggleBox = styled.div`

`

const MenuImg = styled.img`
    width: 50px;
`

const TopBar = ({darkMode, setDarkMode}) => {
    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        document.documentElement.classList.toggle('dark', !darkMode);
    };

    return (
        <TopBarContainer>
            <LogoImg src={LogoLightImg}/>
            <Right>
                <BgChangeBtn>
                    {/*<ToggleImg src={SunLightImg}/>*/}
{/*                    <ToggleBox onClick={toggleDarkMode} darkMode={darkMode}>
                        {darkMode ? <MoonLightImg size={22} /> : <MoonDarkImg size={22}/>}
                    </ToggleBox>*/}
                    <ToggleBox onClick={toggleLightMode} darkMode={darkMode}>
                        <ToggleImg src={darkMode ? SunDarkImg : SunLightImg} />
                    </ToggleBox>
                    <ToggleBox onClick={toggleDarkMode} darkMode={darkMode}>
                        <ToggleImg src={darkMode ? MoonDarkImg : MoonLightImg} />
                    </ToggleBox>
                </BgChangeBtn>
                <MenuImg src={MenuLightImg}/>
            </Right>
        </TopBarContainer>
    );
}

export default TopBar;
