import styled from "styled-components";
import React from "react";
import SunLightImg from "../images/sun_colored.png";
import MoonLightImg from "../images/moon.png";

const TopBarContainer = styled.div`
    width: 100%;
    //max-width: 1920px;
    height: 80px;
    background: #F0F0F0;
    display: flex;
    align-items: center;
`

const BgChangeBtn = styled.div`
    width: 80px;
    display: flex;
    justify-content: space-between;
`

const ToggleImg = styled.img`
    width: 30px;
    aspect-ratio: 1 / 1;
`

const TopBar = ({darkMode, setDarkMode}) => {
    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        document.documentElement.classList.toggle('dark', !darkMode);
    };

    return (
        <TopBarContainer>
            <BgChangeBtn>
                <ToggleImg src={SunLightImg}/>
                <ToggleImg src={MoonLightImg} onClick={toggleDarkMode} darkMode={darkMode}/>
            </BgChangeBtn>
        </TopBarContainer>
    );
}

export default TopBar;
