import React from "react";
import styled from "styled-components";
import HomeBeforeLight from "../images/home_before-1.png";
import HomeBeforeDark from "../images/home_before.png";
import NewsBeforeLight from "../images/news.png";
import NewsBeforeDark from "../images/news-1.png";
import {ToggleImg} from "./TopBar";

const SideBarContainer = styled.div`
    width: 80px;
    height: 100%;
    padding: 0 1vw;
    background: ${({ darkMode }) => (darkMode ? "#000" : "#F0F0F0")};
    //background: ${({ darkMode }) => (darkMode ? "#777" : "skyblue")};
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    position: fixed;
    top: 0;
    left: 0;
`

const SideBar = ({darkMode, setDarkMode}) => {
    const toggleDarkMode = () => {
        if (!darkMode) {
            setDarkMode(true);
            document.documentElement.classList.add('dark');
        }
    };

    const toggleLightMode = () => {
        if (darkMode) {
            setDarkMode(false);
            document.documentElement.classList.remove('dark');
        }
    };
    return (
        <SideBarContainer darkMode={darkMode}>
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? NewsBeforeDark : NewsBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
            <ToggleImg
                src={darkMode ? HomeBeforeDark : HomeBeforeLight}
                onClick={darkMode ? toggleLightMode : null}
            />
        </SideBarContainer>
    );
}

export default SideBar;
